property.getter:
var sib = this.boxObject.previousSibling;
while (sib) {
  if (sib.localName == "treecol" && sib.boxObject.width > 0 && sib.parentNode == this.parentNode)
    return sib;
  sib = sib.boxObject.previousSibling;
